      out.write("\r\n");
      out.write("<script>\r\n");
      out.write("\r\n");
      out.write("\r\n");
      out.write("\t$(function() {\r\n");
      out.write("\t\tvar isScrollUp = false;\r\n");
      out.write("\t\tvar lastScrollTop;\r\n");
      out.write("\t\tvar unreadCnt = 0;\r\n");
      out.write("\t\t\r\n");
      out.write("\t\tvar divChat = document.getElementById('div_chat');\r\n");
      out.write("\t\t\t\t\t\t\t\t\t\t\t\t//채팅방\r\n");
      out.write("\t\t/* 전송 버튼 클릭 시 */\r\n");
      out.write("\t\t$('#btn_append_row').on(\r\n");
      out.write("\t\t\t\t\"click\",\r\n");
      out.write("\t\t\t\tfunction() {\r\n");
      out.write("\t\t\t\t\t// 라인 추가\r\n");
      out.write("\t\t\t\t\t$('#table_chat').append(\r\n");
      out.write("\t\t\t\t\t\t\t$('<tr>').append(\r\n");
      out.write("\t\t\t\t\t\t\t\t\t$('<td>').append($('#add_name').val()),\r\n");
      out.write("\t\t\t\t\t\t\t\t\t$('<td>').append($('#add_msg').val())));\r\n");
      out.write("\r\n");
      out.write("\t\t\t\t\tif (isScrollUp) {\r\n");
      out.write("\t\t\t\t\t\t// 메뉴가 보이는 상태에서 새로운 라인 추가 시 안 읽은 수 표시\r\n");
      out.write("\t\t\t\t\t\tunreadCnt++;\r\n");
      out.write("\t\t\t\t\t\t$('#btn_scroll_down').html('↓ ' + unreadCnt);\r\n");
      out.write("\t\t\t\t\t}\r\n");
      out.write("\r\n");
      out.write("\t\t\t\t\t// 기본적으로 스크롤 최하단으로 이동 (애니메이션 적용)\r\n");
      out.write("\t\t\t\t\tif (!isScrollUp) {\r\n");
      out.write("\t\t\t\t\t\t$('#div_chat').animate(\r\n");
      out.write("\t\t\t\t\t\t\t\t{\r\n");
      out.write("\t\t\t\t\t\t\t\t\tscrollTop : divChat.scrollHeight\r\n");
      out.write("\t\t\t\t\t\t\t\t\t\t\t- divChat.clientHeight\r\n");
      out.write("\t\t\t\t\t\t\t\t}, 100);\r\n");
      out.write("\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t});\r\n");
      out.write("\r\n");
      out.write("\t\t/* 메뉴 스크롤 ↓ 버튼 클릭 시 */\r\n");
      out.write("\t\t$('#btn_scroll_down').on('click', function() {\r\n");
      out.write("\t\t\t// 마지막으로 보고 있었던 (스크롤을 올리기 시작했던) 위치로 이동\r\n");
      out.write("\t\t\t$('#div_chat').animate({\r\n");
      out.write("\t\t\t\tscrollTop : lastScrollTop\r\n");
      out.write("\t\t\t}, 100);\r\n");
      out.write("\r\n");
      out.write("\t\t\tif (lastScrollTop == divChat.scrollHeight - divChat.clientHeight) {\r\n");
      out.write("\t\t\t\t// 마지막 위치와 스크롤 최하단이 같다면 (새로 추가된 라인이 없다면) 메뉴 숨김\r\n");
      out.write("\t\t\t\t$(\"#menu_scroll_down\").css(\"top\", \"200px\");\r\n");
      out.write("\t\t\t\tisScrollUp = false;\r\n");
      out.write("\t\t\t\tunreadCnt = 0;\r\n");
      out.write("\t\t\t\t$('#btn_scroll_down').html('↓');\r\n");
      out.write("\t\t\t} else {\r\n");
      out.write("\t\t\t\t// 마지막 위치와 스크롤 최하단이 다르다면 (새로 추가된 라인이 있다면) 마지막 위치를 최하단으로 변경\r\n");
      out.write("\t\t\t\tlastScrollTop = divChat.scrollHeight - divChat.clientHeight;\r\n");
      out.write("\t\t\t}\r\n");
      out.write("\t\t})\r\n");
      out.write("\r\n");
      out.write("\t\t/* 스크롤 이벤트 */\r\n");
      out.write("\t\t$(\"#div_chat\").on(\r\n");
      out.write("\t\t\t\t'mousewheel DOMMouseScroll',\r\n");
      out.write("\t\t\t\tfunction(e) {\r\n");
      out.write("\t\t\t\t\tvar E = e.originalEvent, delta = E.wheelDelta || -E.detail;\r\n");
      out.write("\r\n");
      out.write("\t\t\t\t\t// 메뉴를 숨겼을 때만 마지막 위치 저장\r\n");
      out.write("\t\t\t\t\tif (!isScrollUp) {\r\n");
      out.write("\t\t\t\t\t\tlastScrollTop = $(this).scrollTop();\r\n");
      out.write("\t\t\t\t\t}\r\n");
      out.write("\r\n");
      out.write("\t\t\t\t\t// 스크롤이 생겼을 때\r\n");
      out.write("\t\t\t\t\tif ($(this).scrollTop() > 0) {\r\n");
      out.write("\t\t\t\t\t\tif (delta < 0) {\r\n");
      out.write("\t\t\t\t\t\t\t// 스크롤 내리는 이벤트 중 최하단 도달 시 메뉴 숨김 (-1은 오차 제어)\r\n");
      out.write("\t\t\t\t\t\t\tif ($(this).scrollTop() > divChat.scrollHeight\r\n");
      out.write("\t\t\t\t\t\t\t\t\t- divChat.clientHeight - 1) {\r\n");
      out.write("\t\t\t\t\t\t\t\t$(\"#menu_scroll_down\").css(\"top\", \"200px\");\r\n");
      out.write("\t\t\t\t\t\t\t\tisScrollUp = false;\r\n");
      out.write("\t\t\t\t\t\t\t\tunreadCnt = 0;\r\n");
      out.write("\t\t\t\t\t\t\t\t$('#btn_scroll_down').html('↓');\r\n");
      out.write("\t\t\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t\t\t} else {\r\n");
      out.write("\t\t\t\t\t\t\t// 스크롤 올리는 이벤트 발생 시 메뉴 보임\r\n");
      out.write("\t\t\t\t\t\t\t$(\"#menu_scroll_down\").css(\"top\", \"150px\");\r\n");
      out.write("\t\t\t\t\t\t\tisScrollUp = true;\r\n");
      out.write("\t\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t\t}\r\n");
      out.write("\t\t\t\t});\r\n");
      out.write("\t});\r\n");
      out.write("\t\r\n");
      out.write("\t\r\n");
      out.write("\t\r\n");
      out.write("\t\r\n");
      out.write("\t/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////\r\n");
      out.write("\t/////////////////////////////////////        \t\t친구요청 기능 시작           ///////////////////////////////////////////////////////////////\r\n");
      out.write("\t/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////\t\r\n");
      out.write("\r\n");
      out.write("\t// JS 최상위 주소 구하기\r\n");
      out.write("\tfunction getContextPath() {\r\n");
      out.write("\t\tvar hostIndex = location.href.indexOf( location.host ) + location.host.length;\r\n");
      out.write("\t\treturn location.href.substring( hostIndex, location.href.indexOf('/', hostIndex + 1) );\r\n");
      out.write("\t};\r\n");
      out.write("\t\r\n");
      out.write("\t$(document).ready(function(){\r\n");
      out.write("\t\t$(\"#profileRoot\").val( getContextPath() );\r\n");
      out.write("\t});\r\n");
      out.write("\r\n");
      out.write("\r\n");
      out.write("\r\n");
      out.write("\t/* 친구 요청 목록 조회 함수 */\r\n");
