      out.write("\t\t\t\t\t\t\t\t \t<script type=\"text/javascript\" src=\"//dapi.kakao.com/v2/maps/sdk.js?appkey=3a32d3d818847c093a324db2e8ffc840\"></script>\n");
      out.write("\t\t\t\t\t\t\t\t \t\n");
      out.write("\t\t\t\t\t\t\t\t \t<script>\n");
      out.write("\t\t\t\t\t\t\t\t\t// 마커를 담을 배열입니다\n");
      out.write("\t\t\t\t\t\t\t\t\tvar markers = [];\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\tvar mapContainer = document.getElementById('map'), // 지도를 표시할 div \n");
      out.write("\t\t\t\t\t\t\t\t\t    mapOption = {\n");
      out.write("\t\t\t\t\t\t\t\t\t        center: new kakao.maps.LatLng(37.566826, 126.9786567), // 지도의 중심좌표\n");
      out.write("\t\t\t\t\t\t\t\t\t        level: 3 // 지도의 확대 레벨\n");
      out.write("\t\t\t\t\t\t\t\t\t    };  \n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 지도를 생성합니다    \n");
      out.write("\t\t\t\t\t\t\t\t\tvar map = new kakao.maps.Map(mapContainer, mapOption); \n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 장소 검색 객체를 생성합니다\n");
      out.write("\t\t\t\t\t\t\t\t\tvar ps = new kakao.maps.services.Places();  \n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 검색 결과 목록이나 마커를 클릭했을 때 장소명을 표출할 인포윈도우를 생성합니다\n");
      out.write("\t\t\t\t\t\t\t\t\tvar infowindow = new kakao.maps.InfoWindow({zIndex:1});\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 키워드로 장소를 검색합니다\n");
      out.write("\t\t\t\t\t\t\t\t\tsearchPlaces();\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 키워드 검색을 요청하는 함수입니다\n");
      out.write("\t\t\t\t\t\t\t\t\tfunction searchPlaces() {\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    var keyword = document.getElementById('keyword').value;\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    if (!keyword.replace(/^\\s+|\\s+$/g, '')) {\n");
      out.write("\t\t\t\t\t\t\t\t\t        alert('키워드를 입력해주세요!');\n");
      out.write("\t\t\t\t\t\t\t\t\t        return false;\n");
      out.write("\t\t\t\t\t\t\t\t\t    }\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    // 장소검색 객체를 통해 키워드로 장소검색을 요청합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t    ps.keywordSearch( keyword, placesSearchCB); \n");
      out.write("\t\t\t\t\t\t\t\t\t}\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 장소검색이 완료됐을 때 호출되는 콜백함수 입니다\n");
      out.write("\t\t\t\t\t\t\t\t\tfunction placesSearchCB(data, status, pagination) {\n");
      out.write("\t\t\t\t\t\t\t\t\t    if (status === kakao.maps.services.Status.OK) {\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        // 정상적으로 검색이 완료됐으면\n");
      out.write("\t\t\t\t\t\t\t\t\t        // 검색 목록과 마커를 표출합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t        displayPlaces(data);\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        // 페이지 번호를 표출합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t        displayPagination(pagination);\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    } else if (status === kakao.maps.services.Status.ZERO_RESULT) {\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        alert('검색 결과가 존재하지 않습니다.');\n");
      out.write("\t\t\t\t\t\t\t\t\t        return;\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    } else if (status === kakao.maps.services.Status.ERROR) {\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        alert('검색 결과 중 오류가 발생했습니다.');\n");
      out.write("\t\t\t\t\t\t\t\t\t        return;\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    }\n");
      out.write("\t\t\t\t\t\t\t\t\t}\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 검색 결과 목록과 마커를 표출하는 함수입니다\n");
      out.write("\t\t\t\t\t\t\t\t\tfunction displayPlaces(places) {\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    var listEl = document.getElementById('placesList'), \n");
      out.write("\t\t\t\t\t\t\t\t\t    menuEl = document.getElementById('menu_wrap'),\n");
      out.write("\t\t\t\t\t\t\t\t\t    fragment = document.createDocumentFragment(), \n");
      out.write("\t\t\t\t\t\t\t\t\t    bounds = new kakao.maps.LatLngBounds(), \n");
      out.write("\t\t\t\t\t\t\t\t\t    listStr = '';\n");
      out.write("\t\t\t\t\t\t\t\t\t    \n");
      out.write("\t\t\t\t\t\t\t\t\t    // 검색 결과 목록에 추가된 항목들을 제거합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t    removeAllChildNods(listEl);\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    // 지도에 표시되고 있는 마커를 제거합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t    removeMarker();\n");
      out.write("\t\t\t\t\t\t\t\t\t    \n");
      out.write("\t\t\t\t\t\t\t\t\t    for ( var i=0; i<places.length; i++ ) {\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        // 마커를 생성하고 지도에 표시합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t        var placePosition = new kakao.maps.LatLng(places[i].y, places[i].x),\n");
      out.write("\t\t\t\t\t\t\t\t\t            marker = addMarker(placePosition, i), \n");
      out.write("\t\t\t\t\t\t\t\t\t            itemEl = getListItem(i, places[i]); // 검색 결과 항목 Element를 생성합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        // 검색된 장소 위치를 기준으로 지도 범위를 재설정하기위해\n");
      out.write("\t\t\t\t\t\t\t\t\t        // LatLngBounds 객체에 좌표를 추가합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t        bounds.extend(placePosition);\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        // 마커와 검색결과 항목에 mouseover 했을때\n");
      out.write("\t\t\t\t\t\t\t\t\t        // 해당 장소에 인포윈도우에 장소명을 표시합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t        // mouseout 했을 때는 인포윈도우를 닫습니다\n");
      out.write("\t\t\t\t\t\t\t\t\t        (function(marker, title) {\n");
      out.write("\t\t\t\t\t\t\t\t\t            kakao.maps.event.addListener(marker, 'mouseover', function() {\n");
      out.write("\t\t\t\t\t\t\t\t\t                displayInfowindow(marker, title);\n");
      out.write("\t\t\t\t\t\t\t\t\t            });\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t            kakao.maps.event.addListener(marker, 'mouseout', function() {\n");
      out.write("\t\t\t\t\t\t\t\t\t                infowindow.close();\n");
      out.write("\t\t\t\t\t\t\t\t\t            });\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t            itemEl.onmouseover =  function () {\n");
      out.write("\t\t\t\t\t\t\t\t\t                displayInfowindow(marker, title);\n");
      out.write("\t\t\t\t\t\t\t\t\t            };\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t            itemEl.onmouseout =  function () {\n");
      out.write("\t\t\t\t\t\t\t\t\t                infowindow.close();\n");
      out.write("\t\t\t\t\t\t\t\t\t            };\n");
      out.write("\t\t\t\t\t\t\t\t\t        })(marker, places[i].place_name);\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        fragment.appendChild(itemEl);\n");
      out.write("\t\t\t\t\t\t\t\t\t    }\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    // 검색결과 항목들을 검색결과 목록 Elemnet에 추가합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t    listEl.appendChild(fragment);\n");
      out.write("\t\t\t\t\t\t\t\t\t    menuEl.scrollTop = 0;\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    // 검색된 장소 위치를 기준으로 지도 범위를 재설정합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t    map.setBounds(bounds);\n");
      out.write("\t\t\t\t\t\t\t\t\t}\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 검색결과 항목을 Element로 반환하는 함수입니다\n");
      out.write("\t\t\t\t\t\t\t\t\tfunction getListItem(index, places) {\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    var el = document.createElement('li'),\n");
      out.write("\t\t\t\t\t\t\t\t\t    itemStr = '<span class=\"markerbg marker_' + (index+1) + '\"></span>' +\n");
      out.write("\t\t\t\t\t\t\t\t\t                '<div class=\"info\">' +\n");
      out.write("\t\t\t\t\t\t\t\t\t                '   <h5>' + places.place_name + '</h5>';\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    if (places.road_address_name) {\n");
      out.write("\t\t\t\t\t\t\t\t\t        itemStr += '    <span>' + places.road_address_name + '</span>' +\n");
      out.write("\t\t\t\t\t\t\t\t\t                    '   <span class=\"jibun gray\">' +  places.address_name  + '</span>';\n");
      out.write("\t\t\t\t\t\t\t\t\t    } else {\n");
      out.write("\t\t\t\t\t\t\t\t\t        itemStr += '    <span>' +  places.address_name  + '</span>'; \n");
      out.write("\t\t\t\t\t\t\t\t\t    }\n");
      out.write("\t\t\t\t\t\t\t\t\t                 \n");
      out.write("\t\t\t\t\t\t\t\t\t      itemStr += '  <span class=\"tel\">' + places.phone  + '</span>' +\n");
      out.write("\t\t\t\t\t\t\t\t\t                '</div>';           \n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    el.innerHTML = itemStr;\n");
      out.write("\t\t\t\t\t\t\t\t\t    el.className = 'item';\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    return el;\n");
      out.write("\t\t\t\t\t\t\t\t\t}\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 마커를 생성하고 지도 위에 마커를 표시하는 함수입니다\n");
      out.write("\t\t\t\t\t\t\t\t\tfunction addMarker(position, idx, title) {\n");
      out.write("\t\t\t\t\t\t\t\t\t    var imageSrc = 'http://t1.daumcdn.net/localimg/localimages/07/mapapidoc/marker_number_blue.png', // 마커 이미지 url, 스프라이트 이미지를 씁니다\n");
      out.write("\t\t\t\t\t\t\t\t\t        imageSize = new kakao.maps.Size(36, 37),  // 마커 이미지의 크기\n");
      out.write("\t\t\t\t\t\t\t\t\t        imgOptions =  {\n");
      out.write("\t\t\t\t\t\t\t\t\t            spriteSize : new kakao.maps.Size(36, 691), // 스프라이트 이미지의 크기\n");
      out.write("\t\t\t\t\t\t\t\t\t            spriteOrigin : new kakao.maps.Point(0, (idx*46)+10), // 스프라이트 이미지 중 사용할 영역의 좌상단 좌표\n");
      out.write("\t\t\t\t\t\t\t\t\t            offset: new kakao.maps.Point(13, 37) // 마커 좌표에 일치시킬 이미지 내에서의 좌표\n");
      out.write("\t\t\t\t\t\t\t\t\t        },\n");
      out.write("\t\t\t\t\t\t\t\t\t        markerImage = new kakao.maps.MarkerImage(imageSrc, imageSize, imgOptions),\n");
      out.write("\t\t\t\t\t\t\t\t\t            marker = new kakao.maps.Marker({\n");
      out.write("\t\t\t\t\t\t\t\t\t            position: position, // 마커의 위치\n");
      out.write("\t\t\t\t\t\t\t\t\t            image: markerImage \n");
      out.write("\t\t\t\t\t\t\t\t\t        });\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    marker.setMap(map); // 지도 위에 마커를 표출합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t    markers.push(marker);  // 배열에 생성된 마커를 추가합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    return marker;\n");
      out.write("\t\t\t\t\t\t\t\t\t}\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 지도 위에 표시되고 있는 마커를 모두 제거합니다\n");
      out.write("\t\t\t\t\t\t\t\t\tfunction removeMarker() {\n");
      out.write("\t\t\t\t\t\t\t\t\t    for ( var i = 0; i < markers.length; i++ ) {\n");
      out.write("\t\t\t\t\t\t\t\t\t        markers[i].setMap(null);\n");
      out.write("\t\t\t\t\t\t\t\t\t    }   \n");
      out.write("\t\t\t\t\t\t\t\t\t    markers = [];\n");
      out.write("\t\t\t\t\t\t\t\t\t}\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t// 검색결과 목록 하단에 페이지번호를 표시는 함수입니다\n");
      out.write("\t\t\t\t\t\t\t\t\tfunction displayPagination(pagination) {\n");
      out.write("\t\t\t\t\t\t\t\t\t    var paginationEl = document.getElementById('pagination'),\n");
      out.write("\t\t\t\t\t\t\t\t\t        fragment = document.createDocumentFragment(),\n");
      out.write("\t\t\t\t\t\t\t\t\t        i; \n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    // 기존에 추가된 페이지번호를 삭제합니다\n");
      out.write("\t\t\t\t\t\t\t\t\t    while (paginationEl.hasChildNodes()) {\n");
      out.write("\t\t\t\t\t\t\t\t\t        paginationEl.removeChild (paginationEl.lastChild);\n");
      out.write("\t\t\t\t\t\t\t\t\t    }\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t    for (i=1; i<=pagination.last; i++) {\n");
      out.write("\t\t\t\t\t\t\t\t\t        var el = document.createElement('a');\n");
      out.write("\t\t\t\t\t\t\t\t\t        el.href = \"#\";\n");
      out.write("\t\t\t\t\t\t\t\t\t        el.innerHTML = i;\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        if (i===pagination.current) {\n");
      out.write("\t\t\t\t\t\t\t\t\t            el.className = 'on';\n");
      out.write("\t\t\t\t\t\t\t\t\t        } else {\n");
      out.write("\t\t\t\t\t\t\t\t\t            el.onclick = (function(i) {\n");
      out.write("\t\t\t\t\t\t\t\t\t                return function() {\n");
      out.write("\t\t\t\t\t\t\t\t\t                    pagination.gotoPage(i);\n");
      out.write("\t\t\t\t\t\t\t\t\t                }\n");
      out.write("\t\t\t\t\t\t\t\t\t            })(i);\n");
      out.write("\t\t\t\t\t\t\t\t\t        }\n");
      out.write("\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t        fragment.appendChild(el);\n");
      out.write("\t\t\t\t\t\t\t\t\t    }\n");
      out.write("\t\t\t\t\t\t\t\t\t    paginationEl.appendChild(fragment);\n");
