      out.write("\t\n");
      out.write("\t// 투표 게시글작성\n");
      out.write("\t$(\"#voteSubmitBtn\").click(function() {\n");
      out.write("\t\tvar postContent = $(this).parent().find(\"textarea\").val();\n");
      out.write("\t\t//var divBox = $(this).parent(\".box111\");\n");
      out.write("\t\tvar voteOption1 = $(\"#voteOption1\").val();\n");
