      out.write("\t\tvar voteOption3 = $(\"#voteOption3\").val();\n");
      out.write("\n");
      out.write("\t\t\n");
      out.write("\t\t\t\t\t\t\t\t\t\t\n");
      out.write("\t\t$.ajax({\n");
      out.write("\t\t\turl : \"writeComment\",\n");
      out.write("\t\t\tdata : {\"postNo\" : postNo, \"commentContent\" : commentContent },\n");
      out.write("\t\t\ttype : \"post\",\n");
      out.write("\t\t\tsuccess : function(result) {\n");
      out.write("\t\t\t\t\n");
      out.write("\t\t\t\tif (result == \"true\") {\n");
      out.write("\t\t\t\t\treplyCount++;\n");
      out.write("\t\t\t\t\t$(this).parent().parent().parent().prev(\"div\" > \".commentCount\").text(\"댓글\"+replyCount);\n");
      out.write("\t\t\t\t\tconsole.log(\"댓글 작성 성공\");\n");
      out.write("\t\t\t\t\t$(this).parent().prev(\"div\" > \".writeCommentArea\" ).text('');\n");
      out.write("\t\t\t\t\t\n");
      out.write("\t\t\t\t} else {\n");
      out.write("\t\t\t\t\t$(this).parent().prev(\"div\" > \".writeCommentArea\" ).text('');\n");
      out.write("\t\t\t\t}\n");
      out.write("\t\t\t},\n");
